mapper.select:
select warehouse.*,supplier.supplierName brand,item.itemName from vinvin.mainWarehouse0_tool warehouse
    left join vinvin.item item on item.itemID=warehouse.id
    left join vinvin.supplier supplier on supplier.supplierID=item.supplierID;

select warehouse.*,supplier.supplierName brand,item.itemName from vinvin.mainWarehouse0_smallTool warehouse
    left join vinvin.item item on item.itemID=warehouse.id
    left join vinvin.supplier supplier on supplier.supplierID=item.supplierID;

select warehouse.*,supplier.supplierName brand,item.itemName from vinvin.mainWarehouse0_food warehouse
     left join vinvin.item item on item.itemID=warehouse.id
     left join vinvin.supplier supplier on supplier.supplierID=item.supplierID;

select warehouse.*,supplier.supplierName brand,item.itemName from vinvin.mainWarehouse0_commercialItem warehouse
     left join vinvin.item item on item.itemID=warehouse.id
     left join vinvin.supplier supplier on supplier.supplierID=item.supplierID;

select warehouse.*,supplier.supplierName brand,item.itemName from vinvin.mainWarehouse0_others warehouse
     left join vinvin.item item on item.itemID=warehouse.id
     left join vinvin.supplier supplier on supplier.supplierID=item.supplierID;
